/**
 * Render the full Moomin house to the OLED.
 * You can call this from your main render loop.
 */
void render_moominhouse(bool master) {
    // static frame counter is preserved across calls
    static uint16_t frame_counter = 0;

    // Choose which image to display
    const char *img_to_show;

    uint16_t frame_in_cycle = frame_counter % (DAY_NIGHT_CYCLE_FRAMES + BLACK_FRAMES * 2);

    if (!master) {
        img_to_show = moominhus_night_flipped;
        if (false) {
            if (frame_in_cycle < DAY_NIGHT_CYCLE_FRAMES / 2) {
                img_to_show = moominhus_day_flipped;
            } else if (frame_in_cycle < DAY_NIGHT_CYCLE_FRAMES / 2 + BLACK_FRAMES) {
                img_to_show = black;
            } else if (frame_in_cycle < DAY_NIGHT_CYCLE_FRAMES) {
                img_to_show = moominhus_night_flipped;
            } else {
                img_to_show = black;
            }
        }
    } else {
        // master half
        img_to_show = moominhus_night;
        if (false) {
            if (frame_in_cycle < DAY_NIGHT_CYCLE_FRAMES / 2) {
                img_to_show = moominhus_day;
            } else if (frame_in_cycle < DAY_NIGHT_CYCLE_FRAMES / 2 + BLACK_FRAMES) {
                img_to_show = black;
            } else if (frame_in_cycle < DAY_NIGHT_CYCLE_FRAMES) {
                img_to_show = moominhus_night;
            } else {
                img_to_show = black;
            }
        }
    }

    // Set cursor and draw
    oled_set_cursor(0, 0);
    oled_write_raw_P(img_to_show, sizeof(moominhus_day)); // use size of largest image

    // Increment counter for next frame
    frame_counter++;
}
